package assignmentOne;

public class SwapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a,b,c;
		a=10;
		b=20;	
		
		System.out.println("Before Swap a="+a+ " b="+b);
		c=a;
		a=b;
		b=c;
		
		System.out.println("after swap a="+a+" b="+b);
		
		
		

	}

}
